import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { Container, Form, Button, Card, Alert } from 'react-bootstrap';
import { login } from '../services/api';
import { toast } from 'react-toastify';

function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await login({ email, password });
      localStorage.setItem('customer', JSON.stringify(response.data));
      toast.success('Login successful!');
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data || 'Login failed');
      toast.error('Login failed');
    }
  };

  return (
    <Container className="mt-5">
      <Card style={{ 
        maxWidth: '400px', 
        margin: '0 auto',
        backgroundColor: '#1A1A1A',
        border: '1px solid #FFD700',
        color: '#FFFFFF'
      }}>
        <Card.Body>
          <div style={{ textAlign: 'center', marginBottom: '20px' }}>
            <img 
              src={require('../assets/logo.png')} 
              alt="Mahlangu Capital Bank" 
              style={{ width: '80px', height: '80px', borderRadius: '12px' }}
            />
            <h2 style={{ color: '#FFD700', marginTop: '10px' }}>MAHLANGU CAPITAL BANK</h2>
            <p style={{ color: '#FFFFFF', opacity: 0.7 }}>Strength. Security. Wealth.</p>
          </div>
          
          {error && <Alert variant="danger">{error}</Alert>}
          
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label style={{ color: '#FFD700' }}>Email</Form.Label>
              <Form.Control
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
                style={{
                  backgroundColor: '#000000',
                  border: '1px solid #FFD700',
                  color: '#FFFFFF'
                }}
              />
            </Form.Group>
            
            <Form.Group className="mb-3">
              <Form.Label style={{ color: '#FFD700' }}>Password</Form.Label>
              <Form.Control
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
                style={{
                  backgroundColor: '#000000',
                  border: '1px solid #FFD700',
                  color: '#FFFFFF'
                }}
              />
            </Form.Group>
            
            <Button 
              variant="primary" 
              type="submit" 
              className="w-100"
              style={{
                backgroundColor: '#FFD700',
                border: 'none',
                color: '#000000',
                fontWeight: '600',
                padding: '10px'
              }}
            >
              Login
            </Button>
          </Form>
          
          <div className="text-center mt-3">
            <Link to="/register" style={{ color: '#FFD700', textDecoration: 'none' }}>
              Don't have an account? Register
            </Link>
          </div>
        </Card.Body>
      </Card>
    </Container>
  );
}

export default Login;
