import React, { useState, useEffect } from 'react';
import { Container, Form, Button, Card, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { createAccount } from '../services/api';
import { toast } from 'react-toastify';

function CreateAccount() {
  const [formData, setFormData] = useState({
    accountType: 'SAVINGS',
    pin: ''
  });
  const [error, setError] = useState('');
  const [customer, setCustomer] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const customerData = localStorage.getItem('customer');
    if (!customerData) {
      navigate('/');
      return;
    }
    setCustomer(JSON.parse(customerData));
  }, [navigate]);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await createAccount(customer.id, formData);
      toast.success('Account created successfully!');
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data || 'Failed to create account');
      toast.error('Failed to create account');
    }
  };

  return (
    <Container className="mt-5">
      <Card className="mx-auto" style={{ maxWidth: '500px' }}>
        <Card.Body>
          <h2 className="text-center mb-4">Open New Account</h2>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3">
              <Form.Label>Account Type</Form.Label>
              <Form.Select
                name="accountType"
                value={formData.accountType}
                onChange={handleChange}
                required
              >
                <option value="SAVINGS">Savings Account</option>
                <option value="CURRENT">Current Account</option>
              </Form.Select>
            </Form.Group>
            <Form.Group className="mb-3">
              <Form.Label>Set PIN (Optional)</Form.Label>
              <Form.Control
                type="password"
                name="pin"
                value={formData.pin}
                onChange={handleChange}
                maxLength="4"
                placeholder="4-digit PIN"
              />
              <Form.Text className="text-muted">
                PIN is required for withdrawals and transfers
              </Form.Text>
            </Form.Group>
            <Button variant="primary" type="submit" className="w-100">
              Create Account
            </Button>
          </Form>
        </Card.Body>
      </Card>
    </Container>
  );
}

export default CreateAccount;
